mapper.select:
select
from v_gen_info a left join v_gen_info_a1 a1 on a1.info_id = a.info_id left join v_underlying_sec b on a.info_id = b.info_id left join v_underlying_sec_b1 b1 on b1.usecu_id = b.usecu_id left join v_underlying_sec_b2 b2 on b2.usecu_id = b1.b1_usecu_id left join v_interm_sec c on c.info_id = a.info_id left join v_action_detail d on d.info_id = a.info_id left join v_action_option e on e.info_id = a.info_id left join v_action_option_e1 e1 on e1.caoptn_id = e.caoptn_id left join v_action_option_e1a e1a on e1a.e1_caoptn_id = e1.e1_caoptn_id left join v_action_option_e2 e2 on e2.caoptn_id = e.caoptn_id left join v_add_info f on f.info_id = a.info_id where 1=1
and a.corp = #{corp}
limit #{startNo},#{pageSize}
